.skew-button {
    cursor: pointer;
    outline: none;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    position: relative;
    display: inline-block;
    padding: 0;
    border: none;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: #fff;
    height: 50px;
    width: 63px;
    opacity: 0.8;
    border-radius: 4px;
    background-color: #4C50C5;
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.14), 0 2px 1px -1px rgba(0,0,0,0.12), 0 1px 3px 0 rgba(0,0,0,0.2);
    overflow: hidden;
}
.row.relative{
    position: relative;
    width: 100%;
    display: block;
}
.skew-button span {
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    position: relative;
    display: block;
    width: 100%;
    height: 100%;
    padding: 14px 0px;
    z-index: 3;
    color: #FFFFFF;
    font-family: Lato !important;
    font-size: 14px;
    font-weight: bold;
    line-height: 24px;
    text-align: center;
    text-transform: capitalize;
    letter-spacing: 1px;
}
.skew-button:before,
.skew-button span:before {
    content: '';
    position: absolute;
    left: 50%;
    top: 50%;
    -webkit-transform: translate(-50%, -50%) skewX(-20deg);
    transform: translate(-50%, -50%) skewX(-20deg);
    width: 128%;
    height: 53px;
    border: 1px solid #00B98F;
    z-index: 2;
}
.skew-button span:before {
    display: block;
    -webkit-transform: translate(-50%, -50%) skew(-20deg);
    transform: translate(-50%, -50%) skew(-20deg);
    -webkit-transform-origin: center center;
    transform-origin: center center;
    z-indeX: -1;
}
.skew-button:after {
    transition: all 100ms ease-out;
    content: '';
    position: absolute;
    left: -8px;
    top: 0;
    -webkit-transform: skewX(-20deg) scaleX(0);
    transform: skewX(-20deg) scaleX(0);
    width: 128%;
    height: 53px;
    background-color: #00B98F;
    border: 1px solid transparent;
    z-index: 1;
}
.skew-button:hover span:before {
    -webkit-animation: fill .5s ease-out;
    animation: fill .5s ease-out;
    -webkit-animation-fill-mode: forwards;
    animation-fill-mode: forwards;
}
.skew-button:hover:after {
    -webkit-animation: swoosh .7s ease-in;
    animation: swoosh .7s ease-in;
}
.skew-button:active:before {
    background-color: #030d1b;
}

@-webkit-keyframes swoosh {
    0% {
        -webkit-transform-origin: left center;
        transform-origin: left center;
    }
    20% {
        -webkit-transform: skewX(-20deg) scaleX(1);
        transform: skewX(-20deg) scaleX(1);
        -webkit-transform-origin: left center;
        transform-origin: left center;
    }
    21% {
        -webkit-transform-origin: right center;
        transform-origin: right center;
    }
    50% {
        -webkit-transform: skewX(-20deg) scaleX(0);
        transform: skewX(-20deg) scaleX(0);
        -webkit-transform-origin: right center;
        transform-origin: right center;
    }
}

@keyframes swoosh {
    0% {
        -webkit-transform-origin: left center;
        transform-origin: left center;
    }
    20% {
        -webkit-transform: skewX(-20deg) scaleX(1);
        transform: skewX(-20deg) scaleX(1);
        -webkit-transform-origin: left center;
        transform-origin: left center;
    }
    21% {
        -webkit-transform-origin: right center;
        transform-origin: right center;
    }
    50% {
        -webkit-transform: skewX(-20deg) scaleX(0);
        transform: skewX(-20deg) scaleX(0);
        -webkit-transform-origin: right center;
        transform-origin: right center;
    }
}
